#include <iostream>using namespace std;int main () {        cout << "Enter the size of the array: " << endl;        int size;        cin >> size;        if(size < 0){                cout << "ERROR: you entered an incorrect value for the array size!";		return 0;                }        int array [size];        cout << "Enter the numbers in the array, seperated by a space, and press enter: " << endl;        //Run through different spots in the array        for(int i =0; i < size; i++){                cin >> array[i];        }        //determine if increasing or not       int count =0; //have to leave this on outside so it doesnt reset	 for(int i=0; i <size; i++){		if(array[i] < array [i+1])			count ++;		if(count==size  && i ==(size-1))			cout << "This IS an increasing array!" <<endl;		if(count != (size) && i == (size-1))			cout << "This is NOT an increasing array!"<<endl;	}	//print list        for(int i =0; i <size; i++){        	cout << array[i] << " ";        }        return 0;}